import Svg, { Line, Circle } from "react-native-svg";
import { StyleSheet, View, Text, TextInput } from "react-native";
import { Lines } from "./Lines";
const words = require("../assets/word.json");
import {
  TrebleEasy,
  TrebleMedium,
  TrebleHard,
  BassEasy,
  BassMedium,
  BassHard
} from "./CYnums";
import TrebleClef from "../images/TrebleClef.svg";
import { Playground } from "./Playground";

let outsideLetters = [];
let musicalLetters = [];

function getNote() {
  //pick random word from word.json
  musicalLetters = [];
  outsideLetters = [];

  let letters = words.words[Math.floor(Math.random() * 74)];
  letters = letters.split("");
  const musicalAlphabet = words.musicLetters;

  //check if letter is part of musical alphabet
  for (let i = 0; i < letters.length; i++) {
    if (musicalAlphabet.includes(letters[i])) {
      musicalLetters.push(letters[i]);
      outsideLetters.push(i);
    } else {
      outsideLetters.push(letters[i]);
      musicalLetters.push(i);
    }
  }
  console.log(outsideLetters);
  console.log(musicalLetters);
  return outsideLetters, musicalLetters;
}

let cy = [];
function isNumber(val) {
  // negative or positive
  return /^[-]?\d+$/.test(val);
}

function getCYTrebleHard() {
  cy = [];
  //use musicalLetters to find object key
  for (let i = 0; i < musicalLetters.length; i++) {
    if (!isNumber(musicalLetters[i])) {
      cy.push(TrebleHard[musicalLetters[i]]);
    } else {
      cy.push(false);
    }
  }
  console.log(cy);
}

const DrawStaff = () => {
  getNote();
  getCYTrebleHard();
  const list = () => {
    return cy.map((element, i) => {
      if (!cy[i]) {
        console.log(false);
        return (
          <View>
            <Svg key={i} style={styles.staves}>
              <Line
                x1="0"
                y1="16"
                x2="400"
                y2="16"
                stroke="black"
                strokeWidth="1"
              />
              <Line
                x1="0"
                y1="32"
                x2="400"
                y2="32"
                stroke="black"
                strokeWidth="1"
              />
              <Line
                x1="0"
                y1="48"
                x2="400"
                y2="48"
                stroke="black"
                strokeWidth="1"
              />
              <Line
                x1="0"
                y1="64"
                x2="400"
                y2="64"
                stroke="black"
                strokeWidth="1"
              />
              <Line
                x1="0"
                y1="80"
                x2="400"
                y2="80"
                stroke="black"
                strokeWidth="1"
              />
            </Svg>
            <Text placeholder="guess your letter" style={styles.text}>
              {outsideLetters[i]}
            </Text>
          </View>
        );
      } else {
        return (
          <Svg key={i} style={styles.staves}>
            <Circle
              cx="100"
              cy={cy[i]}
              r="5"
              stroke="black"
              strokeWidth="2.5"
              fill="black"
            />
            <Line
              x1="0"
              y1="16"
              x2="400"
              y2="16"
              stroke="black"
              strokeWidth="1"
            />
            <Line
              x1="0"
              y1="32"
              x2="400"
              y2="32"
              stroke="black"
              strokeWidth="1"
            />
            <Line
              x1="0"
              y1="48"
              x2="400"
              y2="48"
              stroke="black"
              strokeWidth="1"
            />
            <Line
              x1="0"
              y1="64"
              x2="400"
              y2="64"
              stroke="black"
              strokeWidth="1"
            />
            <Line
              x1="0"
              y1="80"
              x2="400"
              y2="80"
              stroke="black"
              strokeWidth="1"
            />
          </Svg>
        );
      }
    });
  };

  return <View style={styles.staves}>{list()}</View>;
};

const NoteInputs = () => {
  const listNotes = () => {
    return musicalLetters.map((element, i) => {
      if (isNumber(musicalLetters[i])) {
        console.log("isNumber");
      } else {
        return <TextInput key={i} style={styles.textInput}></TextInput>;
      }
    });
  };
  return <View>{listNotes()}</View>;
};

const styles = StyleSheet.create({
  staves: {
    flex: 1,
    flexDirection: "row",
    marginTop: 50,
    height: 100,
    padding: 10
  },
  text: {
    fontSize: 40,
    marginTop: 50,
    flex: 1
  },
  textInput: {
    borderBottomColor: "darkblue",
    borderBottomWidth: 3,
    flex: 1
  }
});

export { DrawStaff, NoteInputs };
