import { StyleSheet, View, Text } from "react-native";
import { DrawStaff, NoteInputs } from "./components/DrawStaff";
import Playground from "./components/Playground";
import TrebleClef from "./images/TrebleClef.svg";
import BassClef from "./images/BassClef.svg";

export default function App() {
  return (
    <View style={styles.container}>
      <TrebleClef height={100} width={100} />
      <DrawStaff></DrawStaff>
      <NoteInputs style={styles.input}></NoteInputs>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "lightblue"

    // flexDirection: "column"
  },
  staves: {
    flex: 1,
    flexDirection: "row"
  },
  drawstaff: {
    flex: 1
  },
  treble: {
    flex: 1,
    position: "absolute",
    marginTop: 110
  },
  input: {
    height: 300
  }
});
