import { StyleSheet, View, Text } from "react-native";
import { DrawStaff, NoteInputs } from "./components/DrawStaff";

export default function App() {
  return (
    <View style={styles.container}>
      <DrawStaff />
      <NoteInputs style={styles.input} />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "lightblue"

    // flexDirection: "column"
  },
  staves: {
    flex: 1,
    flexDirection: "row"
  },
  drawstaff: {
    flex: 1
  },
  treble: {
    flex: 1,
    position: "absolute",
    marginTop: 110
  },
  input: {
    padding: 100
  }
});
